import React from "react";
import styled from "styled-components";
import { BaseContainer } from "../../helpers/layout";
import { getDomain } from "../../helpers/getDomain";
import User from "../shared/models/User";
import { withRouter } from "react-router-dom";
import { Button } from "../../views/design/Button";
import {ErrorCode} from "../shared/ErrorHandler/ErrorHandler"
import Player from "../../views/Player";



const FormContainer = styled.div`
  margin-top: 2em;
  display: flex;
  flex-direction: column;
  align-items: center;
  min-height: 300px;
  justify-content: center;
`;

const Form = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: 60%;
  height: 375px;
  font-size: 16px;
  font-weight: 300;
  padding-left: 37px;
  padding-right: 37px;
  border-radius: 5px;
  background: linear-gradient(rgb(27, 124, 186), rgb(2, 46, 101));
  transition: opacity 0.5s ease, transform 0.5s ease;
`;

const InputField = styled.input`
  &::placeholder {
    color: rgba(255, 255, 255, 0.2);
  }
  height: 35px;
  padding-left: 15px;
  margin-left: -4px;
  border: none;
  border-radius: 20px;
  margin-bottom: 20px;
  background: rgba(255, 255, 255, 0.2);
  color: white;
`;

const Label = styled.label`
  color: white;
  margin-bottom: 10px;
  text-transform: uppercase;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 20px;
`;

const Container = styled.div`
  margin: 6px 0;
  width: 280px;
  padding: 10px;
  border-radius: 6px;
  display: flex;
  align-items: center;
  border: 1px solid #ffffff26;
`;

const UserName = styled.div`
  font-weight: lighter;
  margin-left: 5px;
`;

const Id = styled.div`
  margin-left: auto;
  margin-right: 10px;
  font-weight: bold;
`;

const Status = styled.div`
  margin-left: auto;
  margin-right: 10px;
  font-weight: bold;
`;

const Birthday = styled.div`
  margin-left: auto;
  margin-right: 10px;
  font-weight: bold;
`;

const CreationDate = styled.div`
  margin-left: auto;
  margin-right: 10px;
  font-weight: bold;
`;



/**
 * Classes in React allow you to have an internal state within the class and to have the React life-cycle for your component.
 * You should have a class (instead of a functional component) when:
 * - You need an internal state that cannot be achieved via props from other parent components
 * - You fetch data from the server (e.g., in componentDidMount())
 * - You want to access the DOM via Refs
 * https://reactjs.org/docs/react-component.html
 * @Class
 */
class PlayerPageEdit extends React.Component {

    /**
     * If you don’t initialize the state and you don’t bind methods, you don’t need to implement a constructor for your React component.
     * The constructor for a React component is called before it is mounted (rendered).
     * In this case the initial state is defined in the constructor. The state is a JS object containing two fields: name and username
     * These fields are then handled in the onChange() methods in the resp. InputFields
     */
    constructor() { var us = new User( {
        "username" : "Loading",
        "birthday": 0,
        "status": "Loading"
    } );
        super();
        this.state = {
            username: null,
            birthday: null,
            user: us
        };
    }

    toValidDate( date){
        var i = 0;
        var year = "";
        var month = "";
        var day = "";
        var foundyear = false;
        var foundmonth = false;
        var foundday = false;
        while(i < date.length && date.charAt(i) !== '-' && date.charAt(i) !== '/'){
            year += date.charAt(i);
            i+= 1
        }
        i += 1;
        while(i < date.length && date.charAt(i) !== '-' && date.charAt(i) !== '/'){
            month += date.charAt(i);
            i+= 1;
        }
        i += 1;
        while(i < date.length && date.charAt(i) !== '-' && date.charAt(i) !== '/'){
            month += date.charAt(i);
            i+= 1;
        }

        //check if format was false
        if( year == "" || month == "" || day == "" || year.length > 4 ||
            parseInt(year ) == NaN || parseInt(month ) == NaN || parseInt(day ) == NaN || parseInt(month) >12 || parseInt(day) >31 ){
            return NaN;
        }

        //adding zeros
        year = parseInt(year).toString();
        for( i = 0; i < 4-year.length; ++i){
            year = " " + year;
        }
        month = parseInt(month).toString();
        for( i = 0; i < 2-month.length; ++i){
            month = " " + month;
        }
        day = parseInt(day).toString();
        for( i = 0; i < 2-day.length; ++i){
            day = " " + day;
        }

        return year + "-"+month + "-" + day;
    }

    backTomain(){
        localStorage.removeItem("lookingAtUser");
        this.props.history.push(`/game`);
    }

    edit(){
        var change;
        var bdy;
        if( !(this.state.birthday == null) ){
            if( this.toValidDate(this.state.birthday) == NaN ){
                alert( "invalid Birthday-Format, please use YYYY-MM-DD");
                return;
            }
            else{
                this.state.birthday = this.toValidDate(this.state.birthday);
            }
        }

        if( this.state.birthday == null){
            bdy = JSON.stringify({
               username: this.state.username,
                birthday: this.state.user.birthday
            });
        }
        else if(this.state.username == null ){
            bdy = JSON.stringify({
                username: this.state.user.username,
                birthday: this.state.birthday
            });
        }
        else{
            bdy = JSON.stringify({
                username: this.state.username,
                birthday: this.state.birthday
            });

        }
        var id = localStorage.getItem("atID");

        fetch(`${getDomain()}/users/`+id, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body: bdy
        }).then(response => {
            if( response.status < 200 || response.status >= 300 ) {
                throw new Error( ErrorCode(response.status) );
            }
            //"return response.json() })
            //".then(returnedUser => {
                this.props.history.push(`/PlayerPage/`+localStorage.getItem("id"));
            })
            .catch(err => {
                if (err.message.match(/Failed to fetch/)) {
                    alert("The server cannot be reached. Did you start it?");
                }  else if (err.message.match( /unauthorized/)) {     //wrong password, do as soon as you know how to
                    alert( 'wrong username or password');
                    this.props.history.push( '/login' );
                } else if(err.message.match(/not_found/)){
                    alert("id not-found");
                } else {
                    alert(`Something went wrong during the login: ${err.message}`);
                }
            });
    }
    /**
     *  Every time the user enters something in the input field, the state gets updated.
     * @param key (the key of the state for identifying the field that needs to be updated)
     * @param value (the value that gets assigned to the identified state key)
     */
    handleInputChange(key, value) {
        // Example: if the key is username, this statement is the equivalent to the following one:
        // this.setState({'username': value});
        this.setState({ [key]: value });
    }


    componentWillMount() {
        var id = localStorage.getItem("atID");
        fetch(`${getDomain()}/users/`+id, {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            }
        }).then(response => {
            if( response.status !== 200 ) {
                throw new Error( ErrorCode(response.status) );
            }
            return response.json() })
            .then(async returnedUser => {
                await new Promise(resolve => setTimeout(resolve, 800));
                const user = new User(returnedUser);
                this.setState({"user": user});
                // store the token into the local storage
            })
            .catch(err => {
                console.log(err);
                alert("Something went wrong fetching the users: " + err);
            });
    }
    /**
     * componentDidMount() is invoked immediately after a component is mounted (inserted into the tree).
     * Initialization that requires DOM nodes should go here.
     * If you need to load data from a remote endpoint, this is a good place to instantiate the network request.
     * You may call setState() immediately in componentDidMount().
